sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }


    //Type appropriate comment here, and begin script below
    var getDepartment = new GlideAjax("GetDepartment");
    getDepartment.addParam('sysparm_name', 'getDepartment');
    getDepartment.addParam('syspram_employeeID', g_form.getValue('request_for'));
    getDepartment.getXML(function(response) {
        var department = response.responseXML.documentElement.getAttribute("answer");
        g_form.setValue('u_from_department', department);
    });
}
